try {
    const candidates = [];
    const addCandidate = (val) => {
      if (val === undefined || val === null) return;
      const text = String(val).trim();
      if (!text) return;
      candidates.push(text);
    };

    const collectFromObject = (obj) => {
      if (!obj || typeof obj !== 'object') return;
      addCandidate(obj.serialNumber);
      addCandidate(obj.serial_number);
      addCandidate(obj.serial);
      addCandidate(obj.id);
    };

    const globalCandidates = [
      window.profileInfo,
      window.profile_info,
      window.profile,
      window.AdsPowerProfile,
      window.apx,
      window.adsPower,
      window.adspower,
      window.AdsPower,
      window.AdsPower?.profileInfo,
      window.apx?.profileInfo,
      window.adsPower?.profileInfo,
      window.adspower?.profileInfo,
      window.AdsPower?.profile_info
    ];
    globalCandidates.forEach(collectFromObject);

    addCandidate(window.serialNumber);
    addCandidate(window.serial_number);

    if (typeof window.name === 'string' && window.name) {
      const match = window.name.match(/serial(?:Number|_number)?[:=]?([\w-]+)/i);
      if (match && match[1]) addCandidate(match[1]);
    }

    try {
      const nav = navigator || window.navigator;
      if (nav && typeof nav.serialNumber !== 'undefined') addCandidate(nav.serialNumber);
      if (nav && typeof nav.serial_number !== 'undefined') addCandidate(nav.serial_number);
    } catch (navErr) {}

    try {
      const ls = window.localStorage;
      if (ls && typeof ls.length === 'number') {
        for (let i = 0; i < ls.length; i += 1) {
          const key = ls.key(i);
          if (!key) continue;
          if (!/serial|profile|ads/i.test(key)) continue;
          let value;
          try {
            value = ls.getItem(key);
          } catch (lsErr) {
            continue;
          }
          if (!value) continue;
          try {
            const parsed = JSON.parse(value);
            collectFromObject(parsed);
          } catch (jsonErr) {
            const match = String(value).match(/serial(?:Number|_number)?["']?\s*[:=]\s*["']?([A-Za-z0-9._-]+)/i);
            if (match && match[1]) addCandidate(match[1]);
          }
        }
      }
    } catch (lsErrOuter) {}

    const ordered = candidates
      .map((text) => text.replace(/\s+/g, '').trim())
      .filter(Boolean)
      .filter((text, idx, arr) => arr.indexOf(text) === idx);

    if (!ordered.length) return '';

    ordered.sort((a, b) => b.length - a.length);
    return ordered[0];
  } catch (e) {
    return '';
  }

try {
    const serial = ('${serial_number}' || '').trim();
    if (!serial) return '';

    const rawText = '${gs_existing_rows}';
    let parsed;
    try {
      parsed = rawText ? JSON.parse(rawText) : undefined;
    } catch (jsonError) {
      parsed = undefined;
    }

    const raw = parsed || (Array.isArray(window.gs_existing_rows) ? { values: window.gs_existing_rows } : {});
    const values = Array.isArray(raw?.values) ? raw.values : Array.isArray(raw) ? raw : [];

    let startRow = 1;
    if (raw && typeof raw.range === 'string') {
      const match = raw.range.match(/^[^!]*!([A-Z]+)(\d+)/);
      if (match && match[2]) {
        startRow = parseInt(match[2], 10) || 1;
      } else {
        const fallback = raw.range.match(/([A-Z]+)(\d+)/);
        if (fallback && fallback[2]) {
          startRow = parseInt(fallback[2], 10) || 1;
        }
      }
    }

    const normalizedSerial = serial.toLowerCase();

    for (let idx = 0; idx < values.length; idx += 1) {
      const row = values[idx];
      const cell = Array.isArray(row) && row.length ? String(row[0] ?? '').trim() : '';
      if (!cell) continue;
      const normalizedCell = cell.toLowerCase();
      if (normalizedCell === normalizedSerial) {
        const rowNumber = startRow + idx;
        return `A${rowNumber}:C${rowNumber}`;
      }
    }

    return '';
  } catch (e) {
    return '';
  }
