try {
    const isAuthedByPath = !location.pathname.startsWith('/login');
    const hasDM = !!document.querySelector('a[href="/channels/@me"]');
    const ok = isAuthedByPath || hasDM;
    return ok ? "true" : "false";   // <‚Äî —Å—Ç—Ä–æ–∫–∞, –Ω–µ boolean
  } catch(e) { return "false"; }

try {
    // –¥–∞—ë–º –∏–Ω—Ç–µ—Ä—Ñ–µ–π—Å—É –¥–æ—Ä–µ–Ω–¥–µ—Ä–∏—Ç—å—Å—è
    await new Promise(r => setTimeout(r, 600));

    // 1) –±–µ—Ä—ë–º –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä –Ω–∏–∂–Ω–µ–π –ø–∞–Ω–µ–ª–∏ –∞–∫–∫–∞—É–Ω—Ç–∞ (–ª–æ–∫–∞–ª—å-–Ω–µ–∑–∞–≤–∏—Å–∏–º–æ)
    const userPanel =
      document.querySelector('section[class*="panels_"]') || // —É–Ω–∏–≤–µ—Ä—Å–∞–ª—å–Ω–æ
      document.querySelector('section[aria-label="User area"]') || // EN
      document.querySelector('section[aria-label="–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å—Å–∫–∞—è –æ–±–ª–∞—Å—Ç—å"]'); // RU

    if (userPanel) {
      // 2) –≤ –Ω–µ–π –±–µ—Ä—ë–º –∞–≤–∞—Ç–∞—Ä —Å aria-label="username, Online/–í —Å–µ—Ç–∏/‚Ä¶"
      const avatar = userPanel.querySelector('[role="img"][aria-label]');
      if (avatar) {
        const label = (avatar.getAttribute('aria-label') || '').trim();
        // –±–µ—Ä—ë–º –≤—Å—ë –î–û –ø–µ—Ä–≤–æ–π –∑–∞–ø—è—Ç–æ–π ‚Äî —ç—Ç–æ –∏–º–µ–Ω–Ω–æ username/handle
        let nick = label.split(',')[0].trim();

        // –ø–æ–¥—á–∏—Å—Ç–∏–º: —É–±–µ—Ä—ë–º —Ö–≤–æ—Å—Ç—ã —Ç–∏–ø–∞ —ç–º–æ–¥–∑–∏ –∏ –ø–æ–≤—Ç–æ—Ä—ã
        // –ø—Ä–∏–º–µ—Ä "rnfwy6tzc íÄ≠rnfwy6tzc" -> "rnfwy6tzc"
        nick = nick
          .replace(/[\u200D\uFE0F]/g, '')          // —Å–ª—É–∂–µ–±–Ω—ã–µ —Å–∏–º–≤–æ–ª—ã —ç–º–æ–¥–∑–∏
          .replace(/\s*[\p{Emoji_Presentation}\p{Extended_Pictographic}].*$/u, '') // –≤—Å—ë –ø–æ—Å–ª–µ —ç–º–æ–¥–∑–∏
          .replace(/^([A-Za-z0-9._-]{2,32}).*$/, '$1') // –µ—Å–ª–∏ –ø–æ–≤—Ç–æ—Ä—è–µ—Ç—Å—è, –±–µ—Ä—ë–º –ø–µ—Ä–≤—ã–π "–¥–∏—Å–∫–æ—Ä–¥–Ω—ã–π" —Ç–æ–∫–µ–Ω
          .trim();

        if (nick) return nick;
      }

      // 3) –∑–∞–ø–∞—Å–Ω–æ–π –≤–∞—Ä–∏–∞–Ω—Ç: —Ç–µ–∫—Å—Ç –∏–∑ –±–ª–æ–∫–∞ nameTag –≤ –Ω–∏–∂–Ω–µ–π –ø–∞–Ω–µ–ª–∏
      const nameEl = userPanel.querySelector('[class*="nameTag"] [class*="title"], [class*="nameTag"]');
      if (nameEl) {
        const raw = (nameEl.textContent || '').trim().replace(/\s+/g, ' ');
        // –≤—ã—Ç–∞—â–∏–º –¥–∏—Å–∫–æ—Ä–¥–Ω—ã–π —Ç–æ–∫–µ–Ω –Ω–∞–ø–æ–¥–æ–±–∏–µ "rnfwy6tzc"
        const m = raw.match(/[A-Za-z0-9._-]{2,32}/);
        if (m) return m[0];
        return raw;
      }
    }

    return "";
  } catch (e) {
    return "";
  }
